import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import TypeIt from 'typeit-react';
import { Button } from '@mui/material';
import { ReactTyped } from 'react-typed';

const Home = () => {
    return (
        <main>
            <div className="container d-flex justify-content-around">
                <Button sx={{ backgroundImage: 'linear-gradient(45deg, #0c58cb, #008ff4, #00b9d5, #00d983, #a8eb12);', borderRadius: '2.5rem', height: '30rem', display: 'flex', justifyContent: 'center', alignItems: 'center', width: '100%', color : 'white', fontSize: '2rem', flexDirection:'column' }}>
                    <div>
                       <ReactTyped strings={[ "Welcome to,", "आपका स्वागत है,", "Bienvenido a,", "Bienvenue à,"]} typeSpeed = {300} backDelay={ 1000 } backSpeed={200} loop />
                    </div>
                    <div>
                        NextBook.
                    </div>
                </Button>
                <div className="container d-flex flex-column">
                    <Container sx={{ backgroundImage: 'linear-gradient(to right top, #d16ba5, #c777b9, #ba83ca, #aa8fd8, #9a9ae1, #8aa7ec, #79b3f4, #69bff8, #52cffe, #41dfff, #46eefa, #5ffbf1)', height: '14.5rem', width: '90%', borderRadius: '2.5rem', display: 'flex', justifyContent: 'center', alignItems: 'center', color: 'white', mb : '0.5rem' }}>
                        Hello
                    </Container>
                    <Container sx={{ backgroundImage: 'background-image: linear-gradient(to right top, #d16ba5, #e76c7f, #e57b58, #cf923b, #a9a836, #91b74c, #74c469, #4cd08b, #47dba6, #47e6c1, #4ff1da, #5ffbf1);', height: '14.5rem', width: '90%', borderRadius: '2.5rem', display: 'flex', justifyContent: 'center', alignItems: 'center', color: 'white' }}>
                        Hello
                    </Container>
                </div>
            </div>

            <Box  className="div-body mt-5" sx={ { fontWeight : 'bold', textAlign : 'center', pt : 4 , px : 10 }}>
                <TypeIt
                    options={{ 
                        strings : ["\"People say the longest sentence to ever be printed in literature belongs to Victor Hugo. The claim is that in Les Misérables there is a sentence which is 823 words long.\""],
                        speed : 60,
                        waitUntilVisible : true,
                        loop : false,
                     }}
                />
            </Box>

            <div className='container mt-5 d-flex text-white justify-content-around div-2'>

                    <Box href="/" sx={{ width : 250, height : 300 , bgcolor :"#f472b6", textAlign: "center", borderRadius : "5%", cursor : "pointer", paddingTop: 17, boxShadow: 20, fontSize: 22 }}>Browse a Book</Box>
                    <Box href="/" sx={{ width : 250, height : 300, bgcolor :"#fbbf24", textAlign: "center", borderRadius : "5%", cursor : "pointer", paddingTop: 17, boxShadow: 20, fontSize: 22 }}>See Requested Books</Box>
                    <Box href="/" sx={{ width : 250, height : 300, bgcolor :"#a3e635", textAlign: "center",borderRadius : "5%", cursor : "pointer", paddingTop: 17, boxShadow: 20, fontSize: 22 }}>Rate And Comment</Box>

            </div>
        </main>
    );
}

export default Home;
